import React from "react";
import path from "path";
import styles from "../style/styles.css";
export default class Item extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "default",
      price: 0,
      pictureAddress: path.join("__dirname", "./placeholder.jpeg"),
    };
  }
  render() {
    console.log(__dirname);
    return (
      <div className="item-component" style={styles}>
        <img
          className="image item-image"
          src={this.state.pictureAddress}
          alt="item"
        />
        <h3 className="label item-label">{this.state.name}</h3>
        <h4 className="label item-price-label">${this.state.price}</h4>
      </div>
    );
  }
}
